import React, { Component } from 'react'
import NewsCard from '../../../NewsCard'
import axios from 'axios';

import Container from '@material-ui/core/Container';
import Divider from '@material-ui/core/Divider';
import '../../../componentsCSS/newsTicker.scss'

import Fade from '@material-ui/core/Fade';

const host = 'http://localhost:3005'

export default class CryptoNews extends Component {
    constructor(){
        super();
        this.state = {
            subject: '',
            fade: false,
            articles:[]
        }
    }

    static getDerivedStateFromProps(nextProps, prevState) {
        console.log('get derived fired')
        if (nextProps.match.params.subject !== prevState.subject){
            const currentSubject = nextProps.match.params.subject
            console.log('axios get')
            axios.get(`${host}/api/news/${currentSubject}`)
            .then((response) => {
                console.log(response.data.articles)
                return{
                    fade: true,
                    articles: response.data.articles
                };
            })
            .catch(function (error) {
                // handle error
                console.log(error);
                return null
            })

       }
       return null;
     }

    componentWillMount(){
        const { match: { params } } = this.props;

        this.setState({subject: params.subject})

        axios.get(`${host}/api/news/${params.subject}`)
            .then((response) => {
                this.setState({
                    fade: true,
                    articles: response.data.articles
                });
                console.log(response.data.articles)

            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })
    }

    render() {
        return (
            <Container className="mt-3" component="main" maxWidth="xl">
                <h1> NEWS PAGE NOT LOGGED IN</h1>
                <Divider />
                <Fade
                in={this.state.fade}
                {...({ timeout: 750 })}
              > 
                    <div className='row mt-3'>
                        {this.state.articles.map((articles, index) => < NewsCard key={index} articles={articles}/>)}
                    </div>
                </Fade>
            </Container>
        )
    }
}
