import React from 'react';
import axios from 'axios';

import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLock } from '@fortawesome/free-solid-svg-icons'

const host = process.env.API_HOST;


export default class SignUp extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      password1: '',
      password2: '',
      emailError: "",
      password2Error: "",
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  checkExists = (event) => {
    const itemName = event.target.name;
    const item = event.target.value;
    axios.post(host + '/api/user/' + item + 'exists', 
    itemName === 'email' ? {email: item} : {username: item});
  }

  validate = () => {
    let emailError = "";
    let password2Error = "";
    let phoneError = "";

    let numberCheck = /^[0-9]{10,11}$/
    let passwordCheck = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[^a-zA-Z0-9])[A-Za-z\d@$#!|><}{/\///\\/.,+;:=/\\[~`()\]^_%*?&]{6,100}$/

    if(!this.state.officePhone.match(numberCheck)){
      phoneError = "Invalid phone number"
    }

    if (!this.state.email.includes('@') || 
    !this.state.email.includes('.')) {
      emailError = 'Invalid email'
    }

    if(this.state.password1 !== this.state.password2){
      password2Error = 'Passwords do not match'
    }

    if (!this.state.password2.match(passwordCheck)) {
      password2Error = 'Password must contain: one lowercase letter, one uppercase letter, one numeric character, one special character, and be at least 6 characters in length.'
    }

    if(emailError || password2Error || phoneError){
      this.setState({emailError, password2Error, phoneError})
      return false
    }
    return true
  }

  render(){
    return (
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <div>
          <FontAwesomeIcon className="mb-3" icon={faLock} size="4x" />
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <form onSubmit={this.handleSubmit} noValidate>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="fname"
                  name="firstName"
                  variant="outlined"
                  required
                  fullWidth
                  id="firstName"
                  label="First Name"
                  autoFocus
                  onChange={this.handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  autoComplete="lname"
                  onChange={this.handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="email"
                  label="Username"
                  name="username"
                  autoComplete="username"
                  onBlur={this.checkExists}
                  onChange={this.handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  onBlur={this.checkExists}
                  onChange={this.handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  name="password1"
                  label="Password"
                  type="password"
                  id="password1"
                  autoComplete="current-password"
                  onChange={this.handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  name="password2"
                  label="Re-enter Password"
                  type="password"
                  id="password2"
                  autoComplete="current-password"
                  onChange={this.handleChange}
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
            >
              Sign Up
            </Button>
            <Grid container justify="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
        <Box mt={5}>
        </Box>
      </Container>
    );
  }
}
